mapper.update:
/**ieda.iaas.common.commonIaas.updateDefaultIaasAccountInfo**/
Update ieda_iaas_account Set 
    default_yn =#{account.defaultYn}
Where id = #{account.id}
AND UPPER(iaas_type)=UPPER(#{account.iaasType})

mapper.select:
/**ieda.iaas.common.commonIaas.selectIngressRulesInfo**/ Select code_name as codeName ,code_value as codeValue ,code_name_kr as codeNameKr ,code_description as codeDescription ,sub_group_code as subGroupCode ,usub_group_code as usubGroupCode ,parent_code as parentCode From ieda_common_code Where parent_code = #{parentCode} AND code_name_kr = #{codeNameKr} AND usub_group_code is null AND sub_group_code is null
And code_value=#{codeValue}
And code_value = 'N'

 /**ieda.iaas.common.commonIaas."selectIngressRulesInfoByUsubGroupCode"**/
 Select 
     code_name as codeName
    ,code_value as codeValue
    ,code_name_kr as codeNameKr
    ,code_description as codeDescription
    ,sub_group_code as subGroupCode
    ,usub_group_code as usubGroupCode
    ,parent_code as parentCode
 From ieda_common_code
Where parent_code= #{parentCode}
  And code_value= #{codeValue} 
  AND usub_group_code= #{uSubGroupCode}
  AND sub_group_code= #{subGroupCode}
